(function(){
    'use strict';
    const User=require('./user.js')
    const Req=require('./request.js')

    function createUser(a,b,c){
        if(a instanceof User) return a;
        return new User(a,b,c);
    }
    const CommonCoreApi=function InitCommonCoreApi(userApiKey,appId,appApiKey,userOptions){
        return new Req(createUser(userApiKey,appId,appApiKey,userOptions));
    }
    Object.defineProperty(CommonCoreApi,'URL',{get:function(){return require('./url.js')}});
    Object.defineProperty(CommonCoreApi,'schemas',{get:function(){return require('./schemas.js')}});
    module.exports=CommonCoreApi;
})();